function slickSlider(){$(".slider").slick({infinite:!0,autoplay:!0,arrows:!0,dots:!0})}function magnificPopup(){$(".lightbox").magnificPopup({type:"image",gallery:{enabled:!0,tPrev:"Vorheriges (Linke Pfeiltaste)",tNext:"Nächstes (Rechte Pfeiltaste)",tCounter:"%curr% von %total%"},tClose:"Schließen (Esc)",tLoading:"Lädt",image:{tError:'<a href="%url%">Das Bild</a> konnte nicht geladen werden.'},ajax:{tError:'<a href="%url%">Die Anfrage</a> ist fehlgeschlagen.'}})}function alert(){swal("Good job!","You clicked the button!","success")}$(document).ready(function(){slickSlider(),magnificPopup()});